    {
        parent::__construct();
    }
    
    /*
     * Get asignacion by idAsignacion
     */
    function get_asignacion($idAsignacion)
    {
        return $this->db->get_where('asignacion',array('idAsignacion'=>$idAsignacion))->row_array();
    }
        
    /*
     * Get all asignacion
     */
    function get_all_asignacion()
    {
        $this->db->order_by('idAsignacion', 'desc');
