    }
    
    /*
     * function to update asignacion
     */
    function update_asignacion($idAsignacion,$params)
    {
        $this->db->where('idAsignacion',$idAsignacion);
        return $this->db->update('asignacion',$params);
    }
    
    /*
     * function to delete asignacion
     */
    function delete_asignacion($idAsignacion)
    {
        return $this->db->delete('asignacion',array('idAsignacion'=>$idAsignacion));
